all_ts["Rhadinophis"] = "Gonyosoma frenatum"
all_ts["Calodactylodes"] = "Calodactylodes aureus"
all_ts["Apostolepis"] = "Apostolepis flavotorquata"
all_ts["Apalone"] = "Apalone ferox"

